@section('content')


<style>
.upload-box {box-shadow: 1px 1px 5px #aaa; border: 1px solid #ddd; border-radius: 2px; padding: 30px 20px; display:inline-block; width:300px; max-width:100%;}
</style>

<div>
	<h3 style="margin-bottom:50px;">Upload failed delivered product</h3>

	<div class="upload-box">
		<h4>Upload (CSV)</h4>
		<form id="send-form" method="post"  role="form" enctype="multipart/form-data" name="send-form">  
			{{ csrf_field() }}
			<input type="file" id="data-file" name="data-file" style="display:none;">
			<label for="data-file" style="display:block; text-align:center; cursor:pointer;">
			<svg width="130" height="111" viewBox="0 0 130 111" fill="none" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><rect width="130" height="111" fill="url(#pattern0)"/><defs><pattern id="pattern0" patternContentUnits="objectBoundingBox" width="1" height="1"><use xlink:href="#image0" transform="translate(0 -0.0855856) scale(0.00195312 0.00228744)"/></pattern><image id="image0" width="512" height="512" xlink:href="data:image/png;base64,"/></defs></svg>
			</label>
			<a href="{{url('/assets/failed_delivery_format.csv')}}" style="font-style:italic; text-decoration:underline; font-size:11px; color:#2366B5;">Click here to download the CSV format</a>
		</form>
	</div>
	
	<div class="loading-box" style="display:none;">Processing, please wait....</div>
</div>



<script>
$(document).ready(function() {
	
	$('#data-file').change(function() {
		
		$('.upload-box').hide();
		$('.loading-box').show();
		
		var formData = new FormData($('#send-form').get(0));
		
		$.ajax({
			url: 'upload-fd-file',
			type: 'POST',
			data: formData,
			async: true,
			dataType: 'json',
			success: function (e) {
				$('.loading-box').hide();
				$('.upload-box').show();
				
				if (e.success == true) {
					toastr.success(e.total_fd + ' Products uploaded successfully.');
				} else {
					toastr.error(e.error_message);
				}
			},
			error: function (e) {
				$('.loading-box').hide();
				$('.upload-box').show();
				
				toastr.error('Something went wrong. Please contact your service provider!');
			},
			cache: false,
			contentType: false,
			processData: false,
			timeout: 120000
		});
		
	});

});

</script>

@endsection